import React, { Component } from "react";
import ImageCard from "./ImageCard";
// import img1 from "../../assets/imgs/img_lg--1.JPG";
// import img2 from "../../assets/imgs/img_lg--2.JPG";
import img3 from "../../assets/imgs/img_lg--3.JPG";
// import img4 from "../../assets/imgs/img_lg--4.JPG";
// import img5 from "../../assets/imgs/img_lg--5.JPG";
import img7 from "../../assets/imgs/img_lg--7.JPG";
// import img8 from "../../assets/imgs/img_lg--8.JPG";
// import img9 from "../../assets/imgs/img_lg--9.JPG";
import img10 from "../../assets/imgs/img_lg--10.JPG";
export default class ImageGallery extends Component {
  static defaultProps = {
    imageList: [
      { alt: "Adriana", src: img10 },
      { alt: "Teresa", src: img7 },
      { alt: "Hara the label", src: img3 },
    ],
  };
  constructor(props) {
    super(props);
    this.state = {
      isDisplayed: Array.from({
        length: this.props.imageList.length,
      }).map((img, i) => (i === 0 ? true : false)),
    };
    this.updateDisplay = this.updateDisplay.bind(this);
    this.handleDirection = this.handleDirection.bind(this);
  }

  componentDidMount() {
    document.addEventListener("keydown", this.handleDirection);
  }
  handleDirection(e) {
    const index = this.state.isDisplayed.findIndex((i) => i === true);
    e.code === "ArrowLeft" ? this.updateDisplay(index, true) : false;
    // function (e) {
    //   e.code === "ArrowLeft" ?
    // }
  }
  updateDisplay(index, back = false) {
    const newDisplayed = this.state.isDisplayed.map((img) => false);
    if (back) {
      if (index - 1 < 0) {
        newDisplayed[this.state.isDisplayed.length] = true;
      } else if (index - 1 > 0) {
        newDisplayed[index - 1] = true;
      }
    }
    if (index + 1 > this.state.isDisplayed.length - 1) {
      newDisplayed[0] = true;
    } else if (index + 1 <= this.state.isDisplayed.length - 1) {
      newDisplayed[index + 1] = true;
    }

    this.setState({ isDisplayed: newDisplayed });
  }

  //   handleClick(e) {
  //     console.log();
  //     this.setState({});
  //   }
  render() {
    const { isDisplayed } = this.state;
    return (
      <div className="ImageGallery">
        <div className="ImageGallery__container">
          {this.props.imageList.map((img, i) => {
            return (
              <ImageCard
                index={i}
                updateDisplay={this.updateDisplay}
                key={i}
                img={img}
                display={isDisplayed[i]}
              />
            );
          })}
        </div>
      </div>
    );
  }
}
