import React, { Component } from "react";
import Header from "../components/Header";
import ImageGallery from "../components/ImageGallery";
import FlexContainer from "./FlexContainer";
import { getContent } from "../helpers/getContent";

import img3 from "../../assets/imgs/img_lg--3.JPG";
import img7 from "../../assets/imgs/img_lg--7.JPG";
import img10 from "../../assets/imgs/img_lg--10.JPG";

import "../../scss/layout/_homeContainer.scss";
export default class HomeContainer extends Component {
  // static defaultProps = {
  //   imageList: [
  //     { alt: "Adriana", src: img10, year: 2019 },
  //     { alt: "Teresa", src: img7, year: 2019 },
  //     { alt: "Hara the label", src: img3, year: 2019 },
  //   ],
  // };
  constructor(props) {
    super(props);
    this.state = {
      currentIndex: 0,
      imageList: [],
    };
    this.updateIndex = this.updateIndex.bind(this);
    this.fetchData = this.fetchData.bind(this);
  }
  updateIndex(i) {
    this.setState({ currentIndex: i });
  }
  async fetchData() {}
  async componentDidMount() {
    const data = await getContent();

    const images = data.map((image) => ({
      src: image.data["desktop-image"].url,
      alt: image.data.title[0].text,
      year: image.data.year[0] ? image.data.year[0].text : "",
      // year: image.data.year[0].text ? image.data.year[0].text : "",
    }));
    console.log(images);
    this.setState({ imageList: images });
  }
  render() {
    return (
      <div className="HomeContainer">
        <FlexContainer>
          <Header
            imageCount={this.state.imageList.length}
            currentIndex={this.state.currentIndex}
          />
          <ImageGallery
            imageList={this.state.imageList}
            updateIndex={this.updateIndex}
          />
        </FlexContainer>
      </div>
    );
  }
}
