import React, { Component } from "react";
import img1 from "../../assets/imgs/img_lg--1.JPG";
import img2 from "../../assets/imgs/img_lg--2.JPG";
import img3 from "../../assets/imgs/img_lg--3.JPG";
export default class ImageGallery extends Component {
  static defaultProps = {
    imageList: [
      { alt: "Ala Ho", src: img1 },
      { alt: "Elliot", src: img2 },
      { alt: "Hara the label", src: img3 },
    ],
  };
  constructor(props) {
    super(props);
    this.state = {
      isDisplayed: Array.from({
        length: this.props.imageList.length,
      }).map((img, i) => (i === 0 ? true : false)),
    };
    this.handleClick = this.handleClick.bind(this);
  }

  handleClick(e) {
    const newArr = [
      //   this.state.isDisplayed[0],
    ];
    console.log(e.currentTarget.getAttribute());
    // this.setState({
    //   isDisplayed: newArr,
    // });
  }
  render() {
    const { isDisplayed } = this.state;
    return (
      <div className="ImageGallery">
        <div className="ImageGallery__container">
          {this.props.imageList.map((img, i) => {
            return (
              <div
                index={i}
                onClick={this.handleClick}
                key={img.src}
                className={
                  isDisplayed[i]
                    ? "ImageGallery__image-set display"
                    : "ImageGallery__image-set"
                }
                // display={isDisplayed[i]}
              >
                <img
                  // srcSet="./assets/img/img_sm--10.JPG 300w, ./assets/img/img_lg--10.JPG 1000w"
                  sizes="(max-width: 56.25em) 20vw, (max-width: 37.5) 30vw, 1000px"
                  alt={img.alt}
                  src={img.src}
                />
              </div>
            );
          })}
        </div>
      </div>
    );
  }
}
