import React, { Component } from "react";
import Header from "../components/Header";
import ImageGallery from "../components/ImageGallery";
import FlexContainer from "./FlexContainer";

import img3 from "../../assets/imgs/img_lg--3.JPG";
import img7 from "../../assets/imgs/img_lg--7.JPG";
import img10 from "../../assets/imgs/img_lg--10.JPG";

import "../../scss/layout/_homeContainer.scss";
export default class HomeContainer extends Component {
  static defaultProps = {
    imageList: [
      { alt: "Adriana", src: img10, year: 2019 },
      { alt: "Teresa", src: img7, year: 2019 },
      { alt: "Hara the label", src: img3, year: 2019 },
    ],
  };
  constructor(props) {
    super(props);
    this.state = {
      currentIndex: 0,
    };
  }
  render() {
    return (
      <div className="HomeContainer">
        <FlexContainer>
          <Header imageCount={this.props.imageList.length} />
          <ImageGallery imageList={this.props.imageList} />
        </FlexContainer>
      </div>
    );
  }
}
