import React, { Component } from "react";
import ImageCard from "./ImageCard";
import RowContainer from "../layout/RowContainer";
import { gsap } from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger.js";
gsap.registerPlugin(ScrollTrigger);

export default class ImageGallery extends Component {
  constructor(props) {
    super(props);
    this.state = {
      galleries: [],
      justifyContent: [],
    };
    this.tl = gsap.timeline({ paused: true });
    this.myElements = [];
  }

  async componentDidMount() {
    console.log(this.myElements);
    this.myElements = this.myElements.map((el) => el.imageRef.current);
    this.tl
      .from(this.myElements, 1.5, {
        y: 30,
        opacity: 0,
        stagger: { amount: 2 },
        onComplete: this.tl.kill(),
        clearProps: "all",
      })
      .play();
  }

  componentWillUnmount() {
    // console.log("unmount");
    this.tl.kill();
  }

  componentDidUpdate(prevProps) {
    if (prevProps.galleries !== this.props.galleries) {
      this.setState({ galleries: this.props.galleries });
    }
  }
  render() {
    const { match, location, history } = this.props;

    return (
      <div className="ImageGallery">
        {this.props.galleries
          .sort((a, b) => (a.position > b.position ? 1 : -1))
          .map((gallery, i) => {
            const { heroSrc, title, slug, year, alt, link, images } = gallery;

            return (
              <RowContainer key={slug}>
                <ImageCard
                  ref={(node) => (this.myElements[i] = node)}
                  index={i}
                  key={i}
                  src={heroSrc}
                  title={title}
                  slug={slug}
                  year={year}
                  alt={alt}
                  link={link}
                  images={images}
                  match={match}
                  location={location}
                  history={history}
                  styles={""}
                />
              </RowContainer>
            );
          })}
      </div>
    );
  }
}
