import React, { Component } from "react";

export default class ImageCard extends Component {
  constructor(props) {
    super(props);
    this.handleClick = this.handleClick.bind(this);
  }

  handleClick(e) {
    this.props.updateDisplay(this.props.index);
  }
  render() {
    const { index, display, img } = this.props;
    return (
      <div
        className={
          display
            ? "ImageGallery__image-set display"
            : "ImageGallery__image-set"
        }
      >
        <div
          index={index}
          onClick={this.handleClick}
          key={src}

          // display={isDisplayed[i]}
        >
          <img
            // srcSet="./assets/img/img_sm--10.JPG 300w, ./assets/img/img_lg--10.JPG 1000w"
            sizes="(max-width: 56.25em) 20vw, (max-width: 37.5) 30vw, 1000px"
            alt={alt}
            src={src}
          />
        </div>
      </div>
    );
  }
}
