import React, { Component } from "react";

export default class ImageCard extends Component {
  constructor(props) {
    super(props);
    this.handleClick = this.handleClick.bind(this);
  }

  handleClick(e) {
    this.props.updateDisplay(this.props.index);
  }
  render() {
    const { index, display, src, alt, home } = this.props;
    let styles = { top: 0, opacity: 0 };
    let activeStyles = {
      position: "absolute",
      top: 0,
      opacity: 1,
      pointerEvents: "all",
      transition: "opacity 0.3s ease",
    };
    return (
      <div className={"ImageCard"}>
        <div
          index={index}
          onClick={this.handleClick}
          key={src}

          // display={isDisplayed[i]}
        >
          <img
            // srcSet="./assets/img/img_sm--10.JPG 300w, ./assets/img/img_lg--10.JPG 1000w"
            sizes="(max-width: 56.25em) 20vw, (max-width: 37.5) 30vw, 1000px"
            alt={alt}
            src={src}
          />
        </div>
      </div>
    );
  }
}
