import React, { Component } from "react";
import Navigation from "./Navigation";
import Counter from "./Counter";
export default class Header extends Component {
  render() {
    const { title, name, counter, center } = this.props;
    const isHome = title && name && counter;
    let isDisplayed;
    isHome ? (isDisplayed = "block") : (isDisplayed = "none");
    return (
      <header
        className="Header"
        style={{ justifyContent: center ? "center" : "space-between" }}
      >
        <div className="Header__title" style={{ display: `${isDisplayed}` }}>
          <span className="welcome">welcome</span>
        </div>
        <div className="Header__content">
          <div className="Header__name" style={{ display: `${isDisplayed}` }}>
            <span>Andrea Gutierrez</span>
          </div>
          <Navigation />
        </div>

        {isHome && (
          <Counter
            style={{ display: `${isDisplayed}` }}
            imageCount={this.props.imageCount}
            currentIndex={this.props.currentIndex}
          />
        )}
      </header>
    );
  }
}
