import React, { Component } from "react";
import ImageCard from "./ImageCard";
import img1 from "../../assets/imgs/img_lg--1.JPG";
import img2 from "../../assets/imgs/img_lg--2.JPG";
import img3 from "../../assets/imgs/img_lg--3.JPG";
export default class ImageGallery extends Component {
  static defaultProps = {
    imageList: [
      { alt: "Ala Ho", src: img1 },
      { alt: "Elliot", src: img2 },
      { alt: "Hara the label", src: img3 },
    ],
  };
  constructor(props) {
    super(props);
    this.state = {
      isDisplayed: Array.from({
        length: this.props.imageList.length,
      }).map((img, i) => (i === 0 ? true : false)),
    };
    this.updateDisplay = this.updateDisplay.bind(this);
  }
  updateDisplay(index) {
    const newDisplayed = this.state.isDisplayed.map((img, i) => {
      if (index + 1 === i) {
        return true;
      }
      if (index + 1 > this.state.isDisplayed.length) {
        return true;
      } else {
        return false;
      }
    });
    console.log(newDisplayed);
    // this.setState({});
  }

  //   handleClick(e) {
  //     console.log();
  //     this.setState({});
  //   }
  render() {
    const { isDisplayed } = this.state;
    return (
      <div className="ImageGallery">
        <div className="ImageGallery__container">
          {this.props.imageList.map((img, i) => {
            return (
              <ImageCard
                index={i}
                updateDisplay={this.updateDisplay}
                key={i}
                img={img}
                display={isDisplayed[i]}
              />
            );
          })}
        </div>
      </div>
    );
  }
}
