import React, { Component } from "react";
import ImageCard from "./ImageCard";
import RowContainer from "../layout/RowContainer";
export default class ImageList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      galleries: [],
    };
    this.generateGalleries = this.generateGalleries.bind(this);
  }

  componentDidUpdate(prevProps) {
    if (prevProps.galleries !== this.props.galleries) {
      this.setState(
        { galleries: this.props.galleries },
        this.generateGalleries
      );
    }
  }

  generateGalleries() {
    this.state.galleries.map((gallery, i) => {
      console.log(gallery);
      const { heroSrc, title, slug, year, alt } = gallery;
      return (
        <RowContainer>
          <ImageCard
            index={i}
            key={i}
            src={heroSrc}
            title={title}
            slug={slug}
            year={year}
            alt={alt}
          />
        </RowContainer>
      );
    });
  }
  render() {
    return (
      <div className="ImageList">
        <RowContainer />
        <RowContainer />
        <RowContainer />
        <RowContainer />
        <RowContainer />
        {/* <h1>1</h1>
        <h1>2</h1>
        <h1>3</h1>
        <h1>4</h1>
        <h1>5</h1>
        <h1>6</h1> */}
      </div>
    );
  }
}
