import React, { Component } from "react";
import FlexContainer from "./FlexContainer";
import Header from "../components/Header";
import ImageList from "../components/ImageList";
import { getContent } from "../helpers/getContent";
export default class PortfolioContainer extends Component {
  constructor(props) {
    super(props);
    this.state = {
      imageList: [],
    };
  }
  async componentDidMount() {
    const data = await getContent();
    const imageSet = data.filter((card) => card.type === "post-gallery");
    const images = imageSet.map((set) => {
      console.log(set);
      const { title, images, link, year } = set.data;
      const gallerySet = {
        title: title[0].text,
        heroSrc: set.data["hero-image"].url,
        year,
        link,
        images,
      };
      return gallerySet;
      // src: image.data["hero-image"].url,
      // mobileSrc: image.data["mobile-image"].url,
      // alt: image.data.title[0].text,
      // year: image.data.year[0] ? image.data.year[0].text : "",
      // link: image.data.link.url,
      // year: image.data.year[0].text ? image.data.year[0].text : "",
    });
    this.setState({ imageList: images });
  }

  render() {
    return (
      <div className="PortfolioContainer">
        <FlexContainer style={{ alignItems: "center" }}>
          <Header center />
          <ImageList />
        </FlexContainer>
      </div>
    );
  }
}
