import React, { Component } from "react";
import ImageCard from "./ImageCard";
import RowContainer from "../layout/RowContainer";
import { getRandomItem } from "../helpers/getRandomItem";
export default class ImageGallery extends Component {
  // static defaultProps = {
  //   justifyContent: Array.from({
  //     length: this.state.galleries.length,
  //   }).map((position) => getRandomItem(["flex-start", "center", "flex-end"])),
  // };
  constructor(props) {
    super(props);
    this.state = {
      galleries: [],
      justifyContent: [],
    };
  }

  componentDidUpdate(prevProps) {
    if (prevProps.galleries !== this.props.galleries) {
      this.setState(
        { galleries: this.props.galleries }
        // this.generateGalleries
      );
    }
  }
  render() {
    return (
      <div className="ImageGallery">
        {this.state.galleries.reverse().map((gallery, i) => {
          const { heroSrc, title, slug, year, alt, link } = gallery;
          return (
            <RowContainer key={slug}>
              <ImageCard
                index={i}
                key={i}
                src={heroSrc}
                title={title}
                slug={slug}
                year={year}
                alt={alt}
                link={link}
                display
              />
            </RowContainer>
          );
        })}
      </div>
    );
  }
}
