import React, { Component } from "react";

export default class FlexContainer extends Component {
  render() {
    let direction;
    this.props.vertical ? (direction = { flexDirection: "column" }) : null;
    return (
      <div className="FlexContainer" style={direction}>
        {this.props.children}
      </div>
    );
  }
}
