import React, { Component } from "react";
export default class FlexContainer extends Component {
  render() {
    let direction;
    this.props.column
      ? (direction = { flexDirection: "column", justifyContent: "center" })
      : (direction = null);
    return (
      <div className="FlexContainer" style={direction}>
        {this.props.children}
      </div>
    );
  }
}
