import React, { Component } from "react";
import Header from "../components/Header";
import Lightbox from "../components/Lightbox";
import Container from "./Container";
import FlexContainer from "./FlexContainer";

export default class HomeContainer extends Component {
  constructor(props) {
    super(props);
    this.state = {
      currentIndex: 0,
      imageSet: this.props.imageSet,
    };
    this.updateIndex = this.updateIndex.bind(this);
  }

  updateIndex(i) {
    this.setState({ currentIndex: i });
  }

  componentDidUpdate(prevProps) {
    if (prevProps.imageSet == this.props.imageSet) {
      this.setState({ imageSet: this.props.imageSet });
    }
  }

  // async componentDidMount() {
  //   console.log("mount");
  //   const data = await getContent();
  //   const imageCards = data.filter((card) => card.type === "post-imagecard");
  //   const images = imageCards.reverse().map((image) => ({
  //     src: image.data["desktop-image"].url,
  //     mobileSrc: image.data["mobile-image"].url,
  //     alt: image.data.title[0].text,
  //     year: image.data.year[0] ? image.data.year[0].text : "",
  //     link: image.data.link.url,
  //     // year: image.data.year[0].text ? image.data.year[0].text : "",
  //   }));
  //   this.setState({ imageSet: images });
  // }

  render() {
    return (
      <div className="HomeContainer">
        <Container>
          <FlexContainer>
            <Header
              title
              name
              counter
              imageCount={this.state.imageSet.length}
              currentIndex={this.state.currentIndex}
            />
            <Lightbox
              imageSet={this.state.imageSet}
              updateIndex={this.updateIndex}
            />
          </FlexContainer>
        </Container>
      </div>
    );
  }
}
